import React , { Component } from 'react';
import Piece from './Piece';

class Square extends Component {
  constructor(props) {
    super(props);

    this.state = {
      board: {
        black: [
                {row: 0, column: 1}, {row: 0, column: 3}, {row: 0, column: 5}, {row: 0, column: 7},
                {row: 1, column: 0}, {row: 1, column: 2}, {row: 1, column: 4}, {row: 1, column: 6},
                {row: 2, column: 1}, {row: 2, column: 3}, {row: 2, column: 5}, {row: 2, column: 7}
        ],
        red: [
                {row: 5, column: 1}, {row: 5, column: 3}, {row: 5, column: 5}, {row: 5, column: 7},
                {row: 6, column: 0}, {row: 6, column: 2}, {row: 6, column: 4}, {row: 6, column: 6},
                {row: 7, column: 1}, {row: 7, column: 3}, {row: 7, column: 5}, {row: 7, column: 7}
        ]
      }
    }
    //{ this.props.hasPiece ? <Piece color={this.props.pieceColor == 0 ? "black" : "white" }/> : null}
    this.handleClick = this.handleClick.bind(this);
    this.self = this;
  }

  handleClick() {
    console.log(this.props.position);
  }

  render() {
    var self = this;
    return (
      <div style={{width: 60, height: 60, backgroundColor: this.props.color, display: "inline-flex"}}
        onClick={this.handleClick}>
          <div style={{width: 50, height: 50, borderRadius: 25, color: this.props.color}}> </div>
      </div>
    );
  }

}

export default Square;
