import React , { Component } from 'react';
import Piece from './Piece';

class Square extends Component {
  constructor(props) {
    super(props);

    this.state = {
      board: this.props.board
    }
    //{ this.props.hasPiece ? <Piece color={this.props.pieceColor == 0 ? "black" : "white" }/> : null}
    this.handleClick = this.handleClick.bind(this);
    this.self = this;
  }

  blackContains(position) {
    for (var i = 0; i < this.state.board.black.length; i++) {
      if (this.state.board.black[i].row === position.row && this.state.board.black[i].column === position.column) {
        return true;
      }
    }
    return false;
  }

  redContains(position) {
    for (var i = 0; i < this.state.board.black.length; i++) {
      if (this.state.board.red[i].row === position.row && this.state.board.red[i].column === position.column) {
        return true;
      }
    }
    return false;
  }

  handleClick() {
    return this.props.position;
  }

  getValidMoves(position) {
    var moves = [];
    for(var i = -1; i < 2; i++) {
      for(var j = -1; j < 2; j++) {
        if(i !== 0 && j !== 0) {
          var pos = {row: position.row + i, column: position.column + j};
          if(!this.blackContains(pos) && !this.redContains(pos)) {
            moves.push(pos);
          }
          var jumpPos = this.getValidJump(position, i, j);
          if(jumpPos.length > 0) {
            moves.push(jumpPos);
          }
        }
      }
    }
    return moves;
  }

  getValidJump(curPos, i, j) {
    var newPos = {row: curPos.row + i, column: curPos.column + j};
    var jumpPos = {row: newPos.row + i, column: newPos.column + j};
    if(this.blackContains(curPos)) {
      if(this.redContains(newPos) && !this.blackContains(jumpPos) && !this.redContains(jumpPos)) {
        return jumpPos;
      }
    }
    if(this.redContains(curPos)) {
      console.log("red contains");
      console.log(this.blackContains(newPos));
      console.log(!this.blackContains(jumpPos));
      if(this.blackContains(newPos) && !this.blackContains(jumpPos) && !this.redContains(jumpPos)) {
        return jumpPos;
      }
    }
    return [];
  }

  render() {

    if(this.state.board.black.includes(this.props.position)) {
      console.log(this.props.position);
    }

    var self = this;
    return (
      <div style={{width: 60, height: 60, backgroundColor: this.props.color, display: "inline-flex"}}
        onClick={this.handleClick}>
          {(this.blackContains(this.props.position) && <Piece color="black"/>)
          || (this.redContains(this.props.position) && <Piece color="red"/>)}
      </div>
    );
  }

}

export default Square;
